import React from "react";
import {createRef , useState } from "react";
function Task() {
  const [tasks , settask ]= useState([]);
  const inputref = createRef()
  const addtask =(e) => {
    e.preventDefault();
    const todo = {
      name: inputref.current.value,
      iscompleted: false,
    };
     settask([...tasks, todo]) 
    inputref.current.value = "";
    console.log(tasks);
  };
  return (
    <div className="taskcon">
      <div className="head">
        <div className="head-title">
          <h1>Tasks</h1>
          <p>Today</p>
        </div>
        <button>View All</button>
      </div>
      <div className="tasksec">
        <form
          className="taskform"
          onSubmit={addtask}
        >
          <input ref={inputref} type="text" className="taskinput" />
          <button >+</button>
        </form>
        <ul className="tasks"></ul>
      </div>
    </div>
  );
}

export default Task;
