import React from "react";

function Task() {
 
  return (
    <div className="taskcon">
      <div className="head">
        <div className="head-title">
          <h1>Tasks</h1>
          <p>Today</p>
        </div>
        <button>View All</button>
      </div>
      <div className="tasksec">
      <form onSubmit={handlesub} className="form">
        <input ref={inputRef} placeholder="todo.." className="input" />
        <button className="button">Send</button>
      </form>
      <ul>
        {todoArr.length > 0 &&
          todoArr.reverse().map((item) => <Lists name={item.name} id={item.id} delbtn={deltodo} chek={item.iscompleted} chektodo={chektodo} edittodo={edittodo} />)}
      </ul>
      </div>
    </div>
  );
}

export default Task;
