import React from "react";
import avatar from "../../images/mheader.png";
import Buttons from "../Buttons/Buttons";
import settinh from '../../images/settin.png'
function Userlist({ img, title, update, cname, cactive, button }) {
     const fullday = React.createRef()
    function getTime() {
        const now = new Date();
        const date = now.getDate() < 10 ? "0" + now.getDate() : now.getDate();
        const month =
          now.getMonth() < 10 ? "0" + (now.getMonth() + 1) : now.getMonth();
        const year = now.getFullYear();
        const hour = now.getHours() < 10 ? "0" + now.getHours() : now.getHours();
        const minute =
          now.getMinutes() < 10 ? "0" + now.getMinutes() : now.getMinutes();
        fullDay.textContent = `${date}/${month}/${year}`;
      
        hourEl.textContent = hour;
        minuteEl.textContent = minute;
        secondEl.textContent = now.getSeconds();
        return `${hour}:${minute}  ${date}.${month}.${year}`;
      }
      setInterval(() => {
        getTime();
      }, 1000);


  return (
    <li className="userlist">
      <div className="userdata">
        <img src={avatar} alt="avatarka" />
        <h1>
          Contact Email not Linked <span>Updated 1 day ago</span>
        </h1>
      </div>
      <div className="customerdata">
        <h2>Tom Cruise <span>on 24.05.2019</span></h2>
        <h2 ref={fullday}>May 26, 2019<span>6:30 PM</span></h2>
        <Buttons btnname={"Normal"} btnstyle={"red"}/>
        <button className="setting"><img src={settinh} alt="btn" height={20} /></button>
      </div>
    </li>
  );
}

export default Userlist;
